import React, {useState} from 'react';
import './../App.css';

const Exchanges = (props) => {

   // data={data_exchanges} data2={data_sell} data3={data_buy} data4={data_update}/>))}

    const data = props.data;
    const data2 = props.data2;
    const data3 = props.data3;
    const data4 = props.data4;

    const mercados = Object.keys(data);

    var acciones = [];



    for(var k =0; k < mercados.length; k++){
      for (var i = 0; i < data[mercados[k]].listed_companies.length; i++) {
        acciones = data4.filter((d) => d.company_name === data.listed_companies[i] ? [...acciones, d] : acciones);
      }
    }
    const cant_acciones = acciones.length;

    var suma_sell = 0;
    var suma_buy = 0;
    var suma_total = 0;

    for (var i = 0; i < acciones.length; i++) {
        suma_sell = suma_sell + data2.map((d) => d.ticker === acciones[i].ticker);
      }


    for (var i = 0; i < acciones.length; i++) {
        suma_buy = suma_buy + data3.map((d) => d.ticker === acciones[i].ticker);
      }  

    suma_total = suma_buy + suma_sell;

    return( 
        <tr>
          <th scope="row">{data.name}</th>
          <td>{suma_buy}</td>
          <td>{suma_sell}</td>
          <td>{suma_total}</td>
          <td>{cant_acciones}</td>
          <td>0</td>
        </tr>

    )

};

export default Exchanges;
